$('.slider-catalog-intro__arrow--next').on('click', function () {
   $('.slider-catalog-intro').slick('slickNext');
});
$('.slider-catalog-intro__arrow--prev').on('click', function () {
   $('.slider-catalog-intro').slick('slickPrev');
});

